function isaddedArray(){
    for(var i = 0;i<4;i++){
        for(var j = 0;j<4;j++){
            added[i][j] = 0;
        }
   }
}
 
function moveLeft(){

    if( !canMoveLeft(board))
        return false;
    
    isaddedArray();

    for(var i = 0;i<4;i++)
        for(var j = 1;j<4;j++){
            if(board[i][j] !=0){
           
                for(var k = 0;k<j;k++){
             
                    if(board[i][k] == 0 && noBlockHorizontal(i , k, j, board)){
                        //move
                        showMoveAnimation(i, j,i,k);
                        board[i][k] = board[i][j];
                        board[i][j] = 0;
                        continue;
                    }
    
                    else if(board[i][k] == board[i][j] && noBlockHorizontal(i , k, j, board)){
                        //move
                        showMoveAnimation(i, j,i,k);
                        //add
                        if(added[i][k]!=0){
                                board[i][k+1] = board[i][j];
                                board[i][j] = 0;
                        }
                        else{
                            board[i][k] += board[i][j];
                            board[i][j] = 0;
                            added[i][k] = 1;
                            score +=board[i][k];
                        }
                        continue;
                    }
                }
            }
        }
    setTimeout("updateBoardView()",200);
    return true;
}
 
function moveRight(){

    if( !canMoveRight(board))
        return false;
    
    isaddedArray();

    for(var i = 0;i<4;i++)
        for(var j = 2;j>=0;j--){
            if(board[i][j] !=0){
                for(var k = 3;k>j;k--){
                    if(board[i][k] == 0 && noBlockHorizontal(i , j, k, board)){
                        //move
                        showMoveAnimation(i, j,i,k);
                        board[i][k] = board[i][j];
                        board[i][j] = 0;
                        continue;
                    }
   
                    else if(board[i][k] == board[i][j] && noBlockHorizontal(i , j, k, board)){
                        //move
                        showMoveAnimation(i, j,i,k);
                        //add
                         if(added[i][k]!=0){
                                board[i][k-1] = board[i][j];
                                board[i][j] = 0;
                        }
                        else{
                            board[i][k] += board[i][j];
                            board[i][j] = 0;
                            added[i][k] = 1;
                            score +=board[i][k];
                        }
                        continue;
                    }
                }
            }
        }
    setTimeout("updateBoardView()",200);
    return true;
}
 
function moveUp(){

    if( !canMoveUp(board))
        return false;
    
    isaddedArray();

    for(var j = 0;j<4;j++)
        for(var i = 1;i<4;i++){
            if(board[i][j] !=0){

                for(var k = 0;k<i;k++){

                    if(board[k][j] == 0 && noBlockVertical(j , k, i, board)){
                        //move
                        showMoveAnimation(i, j,k,j);
                        board[k][j] = board[i][j];
                        board[i][j] = 0;
                        continue;
                    }

                    else if(board[k][j] == board[i][j] && noBlockVertical(j , k, i, board)){
                        //move
                        showMoveAnimation(i, j,k,j);
                        //add
                        if(added[k][j]!=0){
                            board[k+1][j] = board[i][j];
                            board[i][j] = 0;
                        }
                        else{
                            board[k][j] += board[i][j];
                            board[i][j] = 0;
                            added[k][j] = 1;
                            score +=board[k][j];
                        }
                        continue;
                    }
                }
            }
        }
    setTimeout("updateBoardView()",200);
    return true;
}
 
function moveDown(){

    if( !canMoveDown(board))
        return false;
        
    isaddedArray();

    for(var j = 0;j<4;j++)
        for(var i = 2;i>=0;i--){
            if(board[i][j] !=0){

                for(var k = 3;k>i;k--){

                    if(board[k][j] == 0 && noBlockVertical(j , i, k, board)){
                        //move
                        showMoveAnimation(i, j,k,j);
                        board[k][j] = board[i][j];
                        board[i][j] = 0;
                        continue;
                    }

                    else if(board[k][j] == board[i][j] && noBlockVertical(j , i, k, board)){
                        //move
                        showMoveAnimation(i, j,k,j);
                        //add
                        if(added[k][j]!=0){
                            board[k-1][j] = board[i][j];
                            board[i][j] = 0;
                        }
                        else{
                            board[k][j] += board[i][j];
                            board[i][j] = 0;
                            added[k][j] = 1;
                            score +=board[k][j];
                        }
                        continue;
                    }
                }
            }
        }
    setTimeout("updateBoardView()",200);
    return true;
}
